import React, { useState } from "react";
import Drink from "./Drink";

const drinks = [
  {
    id: 1,
    drink: "coffee",
    type: "hot",
    size: "M",
    hasWhippingCream: false,
    milkOption: "whole_milk",
    chocolateSaucePumps: 0,
    price: 2.5,
  },
  {
    id: 2,
    drink: "milk_tea",
    type: "hot",
    size: "S",
    hasWhippingCream: true,
    milkOption: "almond_milk",
    chocolateSaucePumps: 2,
    price: 3.0,
  },
];

function Order() {
  const [items, setItems] = useState([]);

  const addItem = (itemId, quantity) => {
    const item = drinks.find((d) => d.id === itemId);
    const newItems = [...items, { ...item, quantity }];
    setItems(newItems);
  };

  const removeItem = (itemId) => {
    const newItems = items.filter((d) => d.id !== itemId);
    setItems(newItems);
  };

  const calculateTotalPrice = () => {
    return items.reduce((total, item) => total + item.quantity * item.price, 0);
  };

  return (
    <div>
      <h2>Order</h2>
      {items.length === 0 ? (
        <p>No items in order</p>
      ) : (
        <ul>
          {items.map((item) => (
            <li key={item.id}>
              {item.quantity} x {item.drink} ({item.size}, {item.milkOption}) -{" "}
              ${item.quantity * item.price}
              <button onClick={() => removeItem(item.id)}>Remove</button>
            </li>
          ))}
          <li>Total: ${calculateTotalPrice()}</li>
        </ul>
      )}
      <h2>Menu</h2>
      {drinks.map((drink) => (
        <Drink key={drink.id} drink={drink} addItem={addItem} />
      ))}
    </div>
  );
}

export default Order;
