import React, { useState } from "react";
import FoodItem from "./FoodItem";

const Menu = () => {
  const [cartItems, setCartItems] = useState([]);

  const addToCart = (item) => {
    setCartItems([...cartItems, item]);
  };

  return (
    <div className="menu">
      <h2>Menu</h2>
      <div>
        <h3>Coffee</h3>
        <FoodItem
          name="Coffee"
          price={2}
          toppings={["Sugar", "Milk", "Cream"]}
          addToCart={addToCart}
        />
      </div>
      <div>
        <h3>Milk Tea</h3>
        <FoodItem
          name="Milk Tea"
          price={3}
          toppings={["Boba", "Jelly", "Pudding"]}
          addToCart={addToCart}
        />
      </div>
      <div>
        <h3>Sandwich</h3>
        <FoodItem
          name="Sandwich"
          price={5}
          toppings={["Lettuce", "Tomato", "Cheese"]}
          addToCart={addToCart}
        />
      </div>
      <div>
        <h3>Bagel</h3>
        <FoodItem
          name="Bagel"
          price={4}
          toppings={["Cream Cheese", "Butter", "Jam"]}
          addToCart={addToCart}
        />
      </div>
      <hr />
      <div className="cart">
        <h2>Cart</h2>
        {cartItems.length === 0 ? (
          <p>Your cart is empty.</p>
        ) : (
          <ul>
            {cartItems.map((item, index) => (
              <li key={index}>
                {item.name} - ${item.price.toFixed(2)}
              </li>
            ))}
            <p>
              Total: $
              {cartItems.reduce((acc, item) => acc + item.price, 0).toFixed(2)}
            </p>
          </ul>
        )}
      </div>
    </div>
  );
};

export default Menu;
