// Menu.js

import React, { Component } from "react";
import Drink from "./Drink";

const initialDrinks = [
  {
    id: 0,
    drink: "coffee",
    type: "hot",
    size: "M",
    hasWhippingCream: false,
    milkOption: "whole_milk",
    chocolateSaucePumps: 0,
  },
];

class Menu extends Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.state = {
      drinks: initialDrinks,
      order: [],
      totalPrice: 0,
    };
  }

  handleAddDrink = (event, drink) => {
    event.preventDefault();
    this.setState((prevState) => ({
      order: [...prevState.order, drink],
      totalPrice: prevState.totalPrice + drink.price,
    }));
  };

  handleRemoveDrink = (index) => {
    const removedDrink = this.state.order[index];
    this.setState((prevState) => ({
      order: prevState.order.filter((_, i) => i !== index),
      totalPrice: prevState.totalPrice - removedDrink.price,
    }));
  };

  handleClearOrder = () => {
    this.setState({ order: [], totalPrice: 0 });
  };

  handleDrinkUpdate = (index, drink) => {
    const updatedDrinks = [...this.state.drinks];
    updatedDrinks[index] = drink;
    this.setState({ drinks: updatedDrinks });
  };

  handleChange = (drink) => {
    console.log(drink);
    let { drinks } = this.state;
    for (let i = 0; i < drinks.length; i++) {
      console.log(drinks[i]);
      if (drinks[i].id === drink.id) {
        drinks[i].drink = drink.drink;
        drinks[i].type = drink.type;
        drinks[i].size = drink.size;
        drinks[i].hasWhippingCream = drink.hasWhippingCream;
        drinks[i].milkOption = drink.milkOption;
        drinks[i].price = drink.price;
      }
    }
    this.setState({ drinks });
  };

  updateOrderTotalPrice = () => {
    let { drinks } = this.state;
    totalOrderPrice = 0;
    for (let i = 0; i < drinks.length; ++i) {
      totalOrderPrice += drinks[i].price;
    }
    tax = totalOrderPrice 
  };
  render() {
    const { drinks, order, totalPrice } = this.state;
    return (
      <div>
        <h2>Menu</h2>
        {drinks.map((drink, index) => (
          <form
            key={index}
            onSubmit={(event) => this.handleAddDrink(event, drink)}
          >
            <Drink
              {...drink}
              onChange={this.handleChange}
              onUpdate={(drink) => this.handleDrinkUpdate(index, drink)}
            />
            <button type="submit">Add to Order</button>
          </form>
        ))}
        <h2>Order</h2>
        {order.length > 0 ? (
          <div>
            {order.map((drink, index) => (
              <div key={index}>
                <Drink {...drink} />
                <button onClick={() => this.handleRemoveDrink(index)}>
                  Remove
                </button>
              </div>
            ))}
            <button onClick={this.handleClearOrder}>Clear Order</button>
            <p>Total Price: ${totalPrice.toFixed(2)}</p>
          </div>
        ) : (
          <p>No drinks in order.</p>
        )}
      </div>
    );
  }
}

export default Menu;
