// Menu.js

import React, { Component } from "react";
import Drink from "./Drink";
import { v4 as uuidv4 } from "uuid";

const initialDrink = {
  drink: "coffee",
  type: "hot",
  size: "M",
  hasWhippingCream: false,
  milkOption: "whole_milk",
  chocolateSaucePumps: 0,
};

class Menu extends Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.state = {
      drink: initialDrink,
      order: [],
      price: {
        totalOrderPrice: 0,
        tax: 0,
        totalOrderPriceAfterTax: 0,
      },
    };
  }

  handleAddDrink = (event, drink) => {
    event.preventDefault();
    const uniqueId = uuidv4();
    drink.id = uniqueId;
    console.log("drink: ", drink);
    this.setState(
      (prevState) => ({
        order: [...prevState.order, drink],
      }),
      () => {
        this.updateOrderTotalPrice();
      }
    );
  };

  handleRemoveDrink = (index) => {
    const removedDrink = this.state.order[index];
    this.setState(
      (prevState) => ({
        order: prevState.order.filter((_, i) => i !== index),
      }),
      () => {
        this.updateOrderTotalPrice();
      }
    );
  };

  handleClearOrder = () => {
    this.setState({ order: [], totalPrice: 0 }, () => {
      this.updateOrderTotalPrice();
    });
  };

  // handleDrinkUpdate = (index, drink) => {
  //   const updatedDrinks = [...this.state.drinks];
  //   updatedDrinks[index] = drink;
  //   this.setState({ drinks: updatedDrinks });
  //   this.updateOrderTotalPrice()
  // };

  handleChange = (drink) => {
    let { order } = this.state;
    for (let i = 0; i < order.length; i++) {
      if (order[i].id === drink.id) {
        order[i].drink = drink.drink;
        order[i].type = drink.type;
        order[i].size = drink.size;
        order[i].hasWhippingCream = drink.hasWhippingCream;
        order[i].milkOption = drink.milkOption;
        order[i].price = drink.price;
      }
    }
    this.setState({ order });
  };

  updateOrderTotalPrice = () => {
    let { order } = this.state;
    console.log(order);
    let totalOrderPrice = 0;
    for (let i = 0; i < order.length; ++i) {
      totalOrderPrice += order[i].price;
      console.log(order[i].price);
    }
    let tax = totalOrderPrice * 0.0725;
    let totalOrderPriceAfterTax = totalOrderPrice + tax;
    console.log(totalOrderPrice);
    console.log(tax);
    console.log(totalOrderPriceAfterTax);
    this.setState({
      price: { totalOrderPrice, tax, totalOrderPriceAfterTax },
    });
  };
  render() {
    const { drinks, order, price } = this.state;
    return (
      <div>
        <h2>Menu</h2>
        <form
          key={index}
          onSubmit={(event) => this.handleAddDrink(event, drink)}
        >
          <Drink
            {...drink}
            onChange={this.handleChange}
            // onUpdate={(drink) => this.handleDrinkUpdate(index, drink)}
          />
          <button type="submit">Add to Order</button>
        </form>
        <h2>Order</h2>
        {order.length > 0 ? (
          <div>
            {order.map((drink, index) => (
              <div key={index}>
                <Drink {...drink} />
                <button onClick={() => this.handleRemoveDrink(index)}>
                  Remove
                </button>
              </div>
            ))}
            <button onClick={this.handleClearOrder}>Clear Order</button>
            <p>Order Price: ${price.totalOrderPrice.toFixed(2)}</p>
            <p>Tax: ${price.tax.toFixed(2)}</p>
            <p>
              Order Price After Tax: ${price.totalOrderPriceAfterTax.toFixed(2)}
            </p>
          </div>
        ) : (
          <p>No drinks in order.</p>
        )}
      </div>
    );
  }
}

export default Menu;
