// Menu.js

import React, { Component } from "react";
import Drink from "./Drink";
import { v4 as uuidv4 } from "uuid";

const initialDrink = {
  drink: "coffee",
  type: "hot",
  size: "S",
  hasWhippingCream: false,
  milkOption: "none",
  chocolateSaucePumps: 0,
};

class Menu extends Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
    this.state = {
      drink: initialDrink,
      order: [],
      orderPrice: {
        totalOrderPrice: 0,
        tax: 0,
        totalOrderPriceAfterTax: 0,
      },
    };
  }
  handleAddDrink = (event, drink) => {
    event.preventDefault();
    const uniqueId = uuidv4();
    drink.id = uniqueId;
    this.setState(
      (prevState) => ({ order: [...prevState.order, drink] }),
      () => {
        this.updateOrderTotalPrice();
      }
    );
  };

  handleRemoveDrink = (index) => {
    this.setState(
      (prevState) => ({
        order: prevState.order.filter((_, i) => i !== index),
      }),
      () => {
        this.updateOrderTotalPrice();
      }
    );
  };

  handleClearOrder = () => {
    this.setState({ order: [], orderPrice: 0 }, () => {
      this.updateOrderTotalPrice();
    });
  };

  // handleDrinkUpdate = (index, drink) => {
  //   const updatedDrinks = [...this.state.drinks];
  //   updatedDrinks[index] = drink;
  //   this.setState({ drinks: updatedDrinks });
  //   this.updateOrderTotalPrice()
  // };

  handleDrinkUpdate = (updatedDrink, price) => {
    let { drink } = this.state;
    drink.drink = updatedDrink.drink;
    drink.type = updatedDrink.type;
    drink.size = updatedDrink.size;
    drink.hasWhippingCream = updatedDrink.hasWhippingCream;
    drink.milkOption = updatedDrink.milkOption;
    drink.price = price;
    this.setState({ drink });
  };

  updateOrderTotalPrice = () => {
    // let { order } = this.state;
    // console.log(order);
    // let totalOrderPrice = 0;
    // for (let i = 0; i < order.length; ++i) {
    //   totalOrderPrice = totalOrderPrice + order[i].props.price;
    // }
    // let tax = totalOrderPrice * 0.0725;
    // let totalOrderPriceAfterTax = totalOrderPrice + tax;
    // this.setState({
    //   orderPrice: { totalOrderPrice, tax, totalOrderPriceAfterTax },
    // });
  };
  render() {
    const { drink, order, orderPrice } = this.state;
    console.log(order);
    return (
      <div>
        <h2>Menu</h2>
        <form onSubmit={(event) => this.handleAddDrink(event, drink)}>
          <Drink
            {...drink}
            onChange={(drink, price) => this.handleDrinkUpdate(index, drink, price)}
          />
          <button type="submit">Add to Order</button>
        </form>
        <h2>Order</h2>
        {order.length > 0 ? (
          <div>
            {order.map((drink, index) => (
              <div key={index}>
                <Drink {...drink} />
                <button onClick={() => this.handleRemoveDrink(index)}>
                  Remove
                </button>
              </div>
            ))}
            <button onClick={this.handleClearOrder}>Clear Order</button>
            <p>Order Price: ${orderPrice.totalOrderPrice.toFixed(2)}</p>
            <p>Tax: ${orderPrice.tax.toFixed(2)}</p>
            <p>
              Order Price After Tax: $
              {orderPrice.totalOrderPriceAfterTax.toFixed(2)}
            </p>
          </div>
        ) : (
          <p>No drinks in order.</p>
        )}
      </div>
    );
  }
}

export default Menu;
