import React, { useState } from "react";


const FoodItem = ({ name, price, toppings, addToCart }) => {
  const [selectedToppings, setSelectedToppings] = useState({});

  const handleToppingChange = (event, type) => {
    const topping = event.target.value;
    const newToppings = { ...selectedToppings };
    if (event.target.checked) {
      newToppings[type] = [...(newToppings[type] || []), topping];
    } else {
      newToppings[type] = newToppings[type].filter((item) => item !== topping);
    }
    setSelectedToppings(newToppings);
  };

  const handleAddToCart = () => {
    const item = {
      name,
      price: price + Object.values(selectedToppings).flat().length * 0.5,
      toppings: selectedToppings,
    };
    addToCart(item);
    setSelectedToppings({});
  };

  return (
    <div>
      <h3>{name}</h3>
      <p>Price: ${price.toFixed(2)}</p>
      {Object.entries(toppings).map(([type, list]) => (
        <div key={type}>
          <p>Select {type}:</p>
          <ul>
            {list.map((topping) => (
              <li key={topping}>
                <label>
                  <input
                    type="checkbox"
                    value={topping}
                    onChange={(event) => handleToppingChange(event, type)}
                    checked={(selectedToppings[type] || []).includes(topping)}
                  />
                  {topping}
                </label>
              </li>
            ))}
          </ul>
        </div>
      ))}
      <button onClick={handleAddToCart}>Add to Cart</button>
    </div>
  );
};

export default FoodItem;
