import FoodItem from "./FoodItem";

const toppings = {
  drink: ["Coke", "Pepsi", "Sprite"],
  type: ["Beef", "Chicken", "Veggie"],
  size: ["Small", "Medium", "Large"],
  hasWhippingCream: ["Yes", "No"],
  milkOption: ["Whole", "Skim", "Almond"],
  chocolateSaucePumps: ["1", "2", "3"],
};

const Menu = ({ items, addToCart }) => {
  return (
    <div>
      {items.map((item) => (
        <FoodItem
          key={item.id}
          name={item.name}
          price={item.price}
          toppings={toppings}
          addToCart={addToCart}
        />
      ))}
    </div>
  );
};
