import React, { useState } from "react";

const FoodItem = ({ name, price, toppings, addToCart }) => {
  const [selectedToppings, setSelectedToppings] = useState([]);

  const handleToppingChange = (event) => {
    const topping = event.target.value;
    const index = selectedToppings.indexOf(topping);
    if (index === -1) {
      setSelectedToppings([...selectedToppings, topping]);
    } else {
      setSelectedToppings(selectedToppings.filter((item) => item !== topping));
    }
  };

  const handleAddToCart = () => {
    const item = {
      name,
      price: price + selectedToppings.length * 0.5,
    };
    addToCart(item);
    setSelectedToppings([]);
  };

  return (
    <div>
      <h3>{name}</h3>
      <p>Price: ${price.toFixed(2)}</p>
      {toppings && (
        <div>
          {toppings.map((toppingObj) => (
            <div key={toppingObj.type}>
              <p>Select {toppingObj.type}:</p>
              <ul>
                {toppingObj.toppings.map((topping) => (
                  <li key={topping}>
                    <label>
                      <input
                        type="checkbox"
                        value={`${toppingObj.type}:${topping}`}
                        onChange={handleToppingChange}
                        checked={selectedToppings.includes(
                          `${toppingObj.type}:${topping}`
                        )}
                      />
                      {topping}
                    </label>
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      )}
      <button onClick={handleAddToCart}>Add to Cart</button>
    </div>
  );
};

export default FoodItem;
