import React, { useState } from "react";
import Drink from "./Drink";

const initialDrinks = [
  {
    drink: "coffee",
    type: "hot",
    size: "M",
    hasWhippingCream: false,
    milkOption: "whole_milk",
    chocolateSaucePumps: 0,
    price: 2.5,
  },
];

function Menu() {
  const [drinks, setDrinks] = useState(initialDrinks);
  const [order, setOrder] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);

  const handleAddDrink = (event, drink) => {

    setOrder([...order, drink]);
    setTotalPrice(totalPrice + drink.price);
  };

  const handleRemoveDrink = (index) => {
    const removedDrink = order[index];
    const newOrder = [...order];
    newOrder.splice(index, 1);
    setOrder(newOrder);
    setTotalPrice(totalPrice - removedDrink.price);
  };

  const handleClearOrder = () => {
    setOrder([]);
    setTotalPrice(0);
  };

  return (
    <div>
      <h2>Menu</h2>
      {drinks.map((drink, index) => (
        <form key={index} onSubmit={(event) => handleAddDrink(event, drink)}>
          <Drink {...drink} />
          <button type="submit">Add to Order</button>
        </form>
      ))}
      <h2>Order</h2>
      {order.length > 0 ? (
        <div>
          {order.map((drink, index) => (
            <div key={index}>
              <Drink {...drink} />
              <button onClick={() => handleRemoveDrink(index)}>Remove</button>
            </div>
          ))}
          <button onClick={handleClearOrder}>Clear Order</button>
          <p>Total Price: ${totalPrice.toFixed(2)}</p>
        </div>
      ) : (
        <p>No drinks in order.</p>
      )}
    </div>
  );
}

export default Menu;
