import React, { Component } from "react";
import Drink from "./Drink";

const initialDrinks = [
  {
    drink: "coffee",
    type: "hot",
    size: "M",
    hasWhippingCream: false,
    milkOption: "whole_milk",
    chocolateSaucePumps: 0,
    price: 2.5,
  },
];

class Menu extends Component {
  constructor(props) {
    super(props);
    this.state = {
      drinks: initialDrinks,
      order: [],
      totalPrice: 0,
    };
  }

  handleAddDrink = (event, drink) => {
    event.preventDefault();
    this.setState((prevState) => ({
      order: [...prevState.order, drink],
      totalPrice: prevState.totalPrice + drink.price,
    }));
  };

  handleRemoveDrink = (index) => {
    const removedDrink = this.state.order[index];
    this.setState((prevState) => ({
      order: prevState.order.filter((_, i) => i !== index),
      totalPrice: prevState.totalPrice - removedDrink.price,
    }));
  };

  handleClearOrder = () => {
    this.setState({ order: [], totalPrice: 0 });
  };

  handleInputChange = (index, field, value) => {
    this.setState((prevState) => {
      const updatedDrinks = [...prevState.drinks];
      updatedDrinks[index][field] = value;
      return { drinks: updatedDrinks };
    });
  };

  render() {
    const { drinks, order, totalPrice } = this.state;
    console.log(d)
    return (
      <div>
        <h2>Menu</h2>
        {drinks.map((drink, index) => (
          <form
            key={index}
            onSubmit={(event) => this.handleAddDrink(event, drink)}
          >
            <Drink
              {...drink}
              onChange={(field, value) =>
                this.handleInputChange(index, field, value)
              }
            />
            <button type="submit">Add to Order</button>
          </form>
        ))}
        <h2>Order</h2>
        {order.length > 0 ? (
          <div>
            {order.map((drink, index) => (
              <div key={index}>
                <Drink {...drink} />
                <button onClick={() => this.handleRemoveDrink(index)}>
                  Remove
                </button>
              </div>
            ))}
            <button onClick={this.handleClearOrder}>Clear Order</button>
            <p>Total Price: ${totalPrice.toFixed(2)}</p>
          </div>
        ) : (
          <p>No drinks in order.</p>
        )}
      </div>
    );
  }
}

export default Menu;
